import React from 'react';
import TodoItem from './todoitem';
import propTypes from 'prop-types';
import './todos.scss';
export default class Todos extends React.Component{
    state = {
        todos : [
          {
            _id : 1,
            title : `lunch @ 1.30 pm`,
            description : `take lunch @ 1.30 pm`,
            completed : false
          },
          {
            _id : 2,
            title : `Call Jigu`,
            description : `call Jigu in the evening`,
            completed : false
          },
          {
            _id : 3,
            title : `enquiry for the second hand car`,
            description : `enquiry for the second hand car`,
            completed : true
          },
        ]
      };
      
    toggleCompleteTodo = (id) => {
        console.log(id)
        this.setState({
            todos : this.state.todos.map(todo => {
                if(id === todo.id){
                    todo.completed = !todo.completed;
                }
                return todo;
            })
        })
    }
    render(){
        return(
            <div className="todo-container">
                <header>
                    Todo List
                </header>
                <div className="todos-list">
                {
                    this.state.todos.map( todo => {
                        return <TodoItem key={todo._id} todo={todo} toggleCompletetodo={this.toggleCompleteTodo} />
                    })
                }
                </div>
            </div>
            
        )
    }
}

Todos.propTypes = {
    todos:propTypes.array.isRequired
}
