import React, { Component } from 'react';
import CounterOutput from '../../components/counteroutput';
import CounterControl from '../../components/countercontrol';
import { connect } from 'reacr-redux'


class Counter extends Component {
  render() {
    return (
      <div style={counterWrapperStyle}>
        <CounterOutput counterValue={this.props.ctr} />
        <div style={counterControlWrapperStyle} >
            <CounterControl label="increment" />
            <CounterControl label="decrement" />
            <CounterControl label="Add 5" />
            <CounterControl label="subtract 5" />
            <CounterControl label="reset" />
        </div>
      </div>
    )
  }
}

const counterWrapperStyle = {
    display:'flex',
    flexDirection : 'column'
}
const counterControlWrapperStyle = {
    display:'flex',
    flexDirection : 'row',
    justifyContent : 'center'
}

const mapStateToProps = state => {
    return {
        ctr : state.counter
    };
}

export default connect(mapStateToProps)(Counter);
