import React from 'react';
import { BrowserRouter , Route , Switch } from 'react-router-dom';
import AboutList from './containers/about/aboutList';
import PassionList from './containers/passion/passion';
import AddAbout from './containers/about/aboutAdd';
import Todos from './containers/todos/todos'
import './App.css';

class App  extends React.Component{
  state = {
    todos : [
      {
        _id : 1,
        title : `lunch @ 1.30 pm`,
        description : `take lunch @ 1.30 pm`,
        completed : false
      },
      {
        _id : 2,
        title : `Call Jigu`,
        description : `call Jigu in the evening`,
        completed : false
      },
      {
        _id : 3,
        title : `enquiry for the second hand car`,
        description : `enquiry for the second hand car`,
        completed : true
      },
    ]
  };

  toggleCompleted 

  render(){
    return (
      <div className="App">
        <BrowserRouter>
          <Switch>
            <Route path="/admin/about/" exact component={AboutList} />
            <Route path="/admin/passion" exact component={PassionList} />
            <Route path="/admin/about/add" exact component={AddAbout} />
            <Route path="/admin/todos" exact render={() => <Todos todos={this.state.todos} />} />
          </Switch>
        </BrowserRouter>
      </div>
    );
  }
}

export default App;
