import React from 'react';
export default class PostForm extends React.Component{
    render(){
        return (
            <React.Fragment>
                <h1>Post Form</h1>
                <div className="postform-wrapper">
                    <form style={formStyle} className="postform-form">
                        <input style={formControl} type="text" placeholder="Add Post title here..." />
                        <textarea style={formControl}
                            placeholder="Add post content here..."></textarea>
                        <input type="submit" style={formControl} value="Submit" />
                    </form>
                </div>
            </React.Fragment>
        );
    }
}

const formStyle = {
    display:'flex',
    flexDirection:'column'
}

const formControl = {
    margin:'.3rem 0'
}
