const initialState = {
    counter : 0,
    results:[]
}

const reducer = (state = initialState,action)=>{
    switch(action.type){
        case "INCREMENT":
            return {
                ...state,
                counter : state.counter + 1
            }
        case "DECREMENT":
            return {
                ...state,
                counter : state.counter - 1
            }
        case "ADD":
            return {
                ...state,
                counter : state.counter + action.payload
            }
        case "SUBTRACT":
            return {
                ...state,
                counter : state.counter - action.payload
            }
        case "RESET":
            return {
                ...state,
                counter : 0
            }
        case "STORE_RESULT":
            return {
                ...state,
                results : state.results.concat({
                    id:Date.now(),
                    value : state.counter
                })
            }
        default:
            return state
    }
}

export default reducer;
